import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import './Dashboard.css'; // Adicione um arquivo CSS para estilização

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [filters, setFilters] = useState({
    codCliente: '',
    rca: '',
    startDate: '',
    endDate: '',
    segmento: '',
    departamento: '',
    codProduto: '',
    supervisor: '',
  });
  
  useEffect(() => {
    const loadData = async () => {
      const response2023 = await fetch('/path/to/dados2023.csv');
      const response2024 = await fetch('/path/to/dados2024.csv');

      const text2023 = await response2023.text();
      const text2024 = await response2024.text();

      Papa.parse(text2023, {
        header: true,
        complete: (results) => {
          setData((prevData) => [...prevData, ...results.data]);
          setFilteredData((prevData) => [...prevData, ...results.data]);
        },
      });

      Papa.parse(text2024, {
        header: true,
        complete: (results) => {
          setData((prevData) => [...prevData, ...results.data]);
          setFilteredData((prevData) => [...prevData, ...results.data]);
        },
      });
    };

    loadData();
  }, []);

  // Função para aplicar filtros
  const applyFilters = () => {
    const filtered = data.filter(item => {
      const date = new Date(item.DT_FAT);
      const startDate = new Date(filters.startDate);
      const endDate = new Date(filters.endDate);
      return (
        (filters.codCliente === '' || item.COD_CLIENTE.includes(filters.codCliente)) &&
        (filters.rca === '' || item.RCA.includes(filters.rca)) &&
        (filters.startDate === '' || filters.endDate === '' || (date >= startDate && date <= endDate)) &&
        (filters.segmento === '' || item.SEGMENTO.includes(filters.segmento)) &&
        (filters.departamento === '' || item.DEPARTAMENTO.includes(filters.departamento)) &&
        (filters.codProduto === '' || item.COD_PROD.includes(filters.codProduto)) &&
        (filters.supervisor === '' || item.NOME_SUPERVISOR.includes(filters.supervisor))
      );
    });
    setFilteredData(filtered);
  };

  // Calcular faturamento total, quantidade de garrafas e quantidade de clientes
  const calculateMetrics = () => {
    const totalFaturamento2023 = data.filter(item => item.DT_FAT.includes('2023')).reduce((acc, item) => acc + parseFloat(item.TOTAL), 0);
    const totalFaturamento2024 = data.filter(item => item.DT_FAT.includes('2024')).reduce((acc, item) => acc + parseFloat(item.TOTAL), 0);
    const totalGarrafas2023 = data.filter(item => item.DT_FAT.includes('2023')).reduce((acc, item) => acc + parseInt(item.QT), 0);
    const totalGarrafas2024 = data.filter(item => item.DT_FAT.includes('2024')).reduce((acc, item) => acc + parseInt(item.QT), 0);
    const totalClientes2023 = new Set(data.filter(item => item.DT_FAT.includes('2023')).map(item => item.COD_CLIENTE)).size;
    const totalClientes2024 = new Set(data.filter(item => item.DT_FAT.includes('2024')).map(item => item.COD_CLIENTE)).size;

    return {
      faturamento: {
        total2023: totalFaturamento2023,
        total2024: totalFaturamento2024,
        growth: ((totalFaturamento2024 - totalFaturamento2023) / totalFaturamento2023) * 100,
      },
      garrafas: {
        total2023: totalGarrafas2023,
        total2024: totalGarrafas2024,
        growth: ((totalGarrafas2024 - totalGarrafas2023) / totalGarrafas2023) * 100,
      },
      clientes: {
        total2023: totalClientes2023,
        total2024: totalClientes2024,
        growth: ((totalClientes2024 - totalClientes2023) / totalClientes2023) * 100,
      },
    };
  };

  const metrics = calculateMetrics();

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <img src="/path/to/logo.png" alt="Logo" className="logo" /> {/* Caminho da logo */}
        <h1>Dashboard Dia Wine</h1>
      </header>
      <section className="overview">
        <h2>Visão Geral</h2>
        <div className="metrics">
          <div className="metric">
            <h3>Faturamento Total</h3>
            <p>2023: R$ {metrics.faturamento.total2023.toFixed(2)}</p>
            <p>2024: R$ {metrics.faturamento.total2024.toFixed(2)}</p>
            <p>Crescimento: {metrics.faturamento.growth.toFixed(2)}%</p>
          </div>
          <div className="metric">
            <h3>Quantidade de Garrafas</h3>
            <p>2023: {metrics.garrafas.total2023}</p>
            <p>2024: {metrics.garrafas.total2024}</p>
            <p>Crescimento: {metrics.garrafas.growth.toFixed(2)}%</p>
          </div>
          <div className="metric">
            <h3>Quantidade de Clientes</h3>
            <p>2023: {metrics.clientes.total2023}</p>
            <p>2024: {metrics.clientes.total2024}</p>
            <p>Crescimento: {metrics.clientes.growth.toFixed(2)}%</p>
          </div>
        </div>
      </section>
      <section className="filters">
        <h2>Filtros</h2>
        <input type="text" placeholder="Código Cliente" value={filters.codCliente} onChange={e => setFilters({ ...filters, codCliente: e.target.value })} />
        <input type="text" placeholder="RCA" value={filters.rca} onChange={e => setFilters({ ...filters, rca: e.target.value })} />
        <input type="date" placeholder="Data Início" value={filters.startDate} onChange={e => setFilters({ ...filters, startDate: e.target.value })} />
        <input type="date" placeholder="Data Fim" value={filters.endDate} onChange={e => setFilters({ ...filters, endDate: e.target.value })} />
        <input type="text" placeholder="Segmento" value={filters.segmento} onChange={e => setFilters({ ...filters, segmento: e.target.value })} />
        <input type="text" placeholder="Departamento" value={filters.departamento} onChange={e => setFilters({ ...filters, departamento: e.target.value })} />
        <input type="text" placeholder="Código Produto" value={filters.codProduto} onChange={e => setFilters({ ...filters, codProduto: e.target.value })} />
        <input type="text" placeholder="Supervisor" value={filters.supervisor} onChange={e => setFilters({ ...filters, supervisor: e.target.value })} />
        <button onClick={applyFilters}>Aplicar Filtros</button>
      </section>
      <section className="data-table">
        <h2>Tabela de Dados</h2>
        <table>
          <thead>
            <tr>
              <th>CNPJ</th>
              <th>Cód Cliente</th>
              <th>Nome Fantasia</th>
              <th>Razão Social</th>
              <th>Bairro</th>
              <th>Município</th>
              <th>Cód Produto</th>
              <th>Produto</th>
              <th>Embalagem</th>
              <th>QT</th>
              <th>Total</th>
              <th>RCA</th>
              <th>Nome Vendedor</th>
              <th>Nome Supervisor</th>
              <th>Cód Dept</th>
              <th>Departamento</th>
              <th>Cond Venda</th>
              <th>DT FAT</th>
              <th>Segmento</th>
            </tr>
          </thead>
          <tbody>
            {filteredData.map((item, index) => (
              <tr key={index}>
                <td>{item.CNPJ}</td>
                <td>{item.COD_CLIENTE}</td>
                <td>{item.NOME_FANTASIA}</td>
                <td>{item.RAZAO_SOCIAL}</td>
                <td>{item.BAIRRO}</td>
                <td>{item.MUNICIPIO}</td>
```javascript
                <td>{item.COD_PROD}</td>
                <td>{item.PRODUTO}</td>
                <td>{item.EMBALAGEM}</td>
                <td>{item.QT}</td>
                <td>{item.TOTAL}</td>
                <td>{item.RCA}</td>
                <td>{item.NOME_VENDEDOR}</td>
                <td>{item.NOME_SUPERVISOR}</td>
                <td>{item.COD_DEPT}</td>
                <td>{item.DEPARTAMENTO}</td>
                <td>{item.COND_VENDA}</td>
                <td>{item.DT_FAT}</td>
                <td>{item.SEGMENTO}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
};

export default Dashboard;
